import { CampaignService } from './../services/campaign.service';
import { Component, OnInit, Input } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { PlayerCharacter } from '../DTOs/PlayerCharacter';
import { CookieService } from 'ngx-cookie-service';

@Component({
  selector: 'app-character-list',
  templateUrl: './character-list.component.html',
  styleUrls: ['./character-list.component.css']
})
export class CharacterListComponent implements OnInit {
  username: any;
  campaignId: any;
  characters: any;
  isCreateCharacterModalOpen: boolean = false;
  createButtonOn: boolean = false;
  fullView: boolean = false;
  selectedCharacter: PlayerCharacter | null = null;
  @Input() type: string = "none";

  constructor(
    private route: ActivatedRoute,
    private campaignService: CampaignService,
    private cookieService: CookieService
        ) 
        {}

  openCreateCharacterModal(): void {
    console.log('Opening modal');
    this.isCreateCharacterModalOpen = true;
    // this.cd.detectChanges();
  }

  closeCreateCharacterModal(): void {
    console.log('Closing modal');
    this.isCreateCharacterModalOpen = false;
    // this.cd.detectChanges();
  }

  onCharacterClick(character: PlayerCharacter): void {
    this.selectedCharacter = character;
    // Further logic can be added here if needed
  }

  ngOnInit(): void {
    // console.log("Initializing character list of type: " + this.type);


    if (!this.type.toLowerCase().includes("no-button")) {
      this.createButtonOn = true;
    }

    if (this.type.toLowerCase().includes("full-view")) {
      this.fullView = true;
    }

    if (this.type.toLowerCase().includes("campaign")){
      this.route.params.subscribe(params => {
        this.campaignId = params['id']; // Get the id from the route parameter
        this.campaignService.getCampaign(this.campaignId).subscribe(
          (data) => {
            // Sort player characters by name in alphabetical order
            if (data.playerCharacters) {
              data.playerCharacters.sort((a: PlayerCharacter, b: PlayerCharacter) => a.characterName.localeCompare(b.characterName));
            }
            this.characters = data.playerCharacters;
          },
          (error) => {
            console.error('Error fetching campaign data:', error);
          }
        );
      });
    }
    else if (this.type.toLowerCase().includes("player")) { 
      this.route.params.subscribe(params => {
        this.username = this.cookieService.get('username'); // Get the id from the route parameter
        this.campaignService.getUserPlayers().subscribe(
          (data) => {
            // Sort player characters by name in alphabetical order
            if (data) {
              data.sort((a: PlayerCharacter, b: PlayerCharacter) => a.characterName.localeCompare(b.characterName));
            }
            this.characters = data;
          },
          (error) => {
            console.error('Error fetching campaign data:', error);
          }
        );
      });
    }
  }

}
<div class="content-container">
  <div class="character-container">
    <div class="create-character-card">
      <button *ngIf="createButtonOn" class="create-character-button" (click)="openCreateCharacterModal()">Create a Character</button>
    </div>
    <div *ngFor="let character of characters" class="character-card">
      <img class="character-image" [src]="character.imageID" alt="{{ character.characterName }}">
      <div class="character-details">
        <h2 [ngClass]="{'clickable-name': fullView}" (click)="fullView ? onCharacterClick(character) : null">{{ character.characterName }}</h2>
        <!-- Add more character details here if needed -->
      </div>
    </div>
  </div>

  <div *ngIf="fullView && selectedCharacter" class="character-detail-section">
    <h2 class="character-name">{{ selectedCharacter.characterName }}</h2>
    <hr class="modern-line">
    <div class="character-attributes-grid">
      <div><p>üí∞: {{ selectedCharacter.g}}GP</p></div>
      <div><p>‚ù§Ô∏è: 97/100HP</p></div>
    </div>
    <hr class="modern-line">
    <div class="character-attributes-grid">
      <!-- Display the abbreviated attributes in two columns, with the abbreviations in bold -->
      <div><strong>STR:</strong> {{ selectedCharacter.str }}</div>
      <div><strong>DEX:</strong> {{ selectedCharacter.dex }}</div>
      <div><strong>WIS:</strong> {{ selectedCharacter.wis }}</div>
      <div><strong>INT:</strong> {{ selectedCharacter.int }}</div>
      <div><strong>CHA:</strong> {{ selectedCharacter.cha }}</div>
      <!-- Add more abbreviated attributes as needed -->
    </div>
  </div>
</div>

<app-create-character-modal *ngIf="isCreateCharacterModalOpen" (close)="closeCreateCharacterModal()"></app-create-character-modal>
